using MailClient.Helpers;
using System;
using System.Collections.Generic;
using System.Data;
using System.Data.SQLite;
using System.IO;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace MailClient.DB
{
    public class DBConnection
    {
        private static string dbFileInBase64 = "";

        private static string dbName = "StudMailDB.db";

        private string connectionString = $"DataSource={dbName};Version=3";

        public static SQLiteConnection Connection;

        public static DBStatus Status;

        public DBConnection()
        {
            DBConnect();
            Open();
        }

        ~DBConnection()
        {
            Close();
        }

        public static void DBConnect()
        {
            if (!File.Exists(dbName))
            {
                using (BinaryWriter writer = new BinaryWriter(new FileStream(dbName, FileMode.Create)))
                {
                    writer.Write(dbFileInBase64);
                }
            }
        }

        public DBStatus Open()
        {
            try
            {
                Connection = new SQLiteConnection(connectionString);
                Connection.Open();

                if (Connection.State != ConnectionState.Open)
                {
                    return DBStatus.Disconnected;
                }

                return DBStatus.Connected;
            }
            catch (SQLiteException)
            {
                return DBStatus.Disconnected;
            }
        }

        public static DBStatus Close()
        {
            Connection.Close();
            return DBStatus.Disconnected;
        }
    }
}
